function changeCursor(){const e=$(".cursor"),t=$(".follower"),a=document.querySelectorAll("a");let n=0,o=0,s=0,c=0;TweenMax.to({},.01,{repeat:-1,onRepeat:()=>{n+=(s-n)/8,o+=(c-o)/8,TweenMax.set(t,{css:{left:n-12,top:o-12}}),TweenMax.set(e,{css:{left:s,top:c}})}}),$(window).mousemove((function(e){s=e.clientX,c=e.clientY})),a.forEach((a=>{a.addEventListener("mouseenter",(function(){e.addClass("active"),t.addClass("active")})),a.addEventListener("mouseleave",(function(){e.removeClass("active"),t.removeClass("active")}))}))}changeCursor(),$(document).ready((()=>{$(".area__left").addClass("_animated")}));